import React, { useState } from 'react';
import Card from './Card';
import "./index.css"
import Sdata from "./Sdata";



const App = () => {

    const [Menudata, Setdata] = useState(Sdata);

    const filterItems = (categery) => {
        const UpdatedList = Sdata.filter((curElem) => {
            return curElem.categery === categery;
        });
        return Setdata(UpdatedList);
    }
    return (<>
        <div className="container">

            <h1 className="heading"> WELCOME TO SHUBHAM FOODS</h1>
            <div className="navbar">
                <button className="btn_grp" onClick={() => { filterItems("Breakfast") }}>Breakfast </button>
                <button className="btn_grp" onClick={() => { filterItems("Lunch") }}>Lunch </button>
                <button className="btn_grp" onClick={() => { filterItems("Dinner") }}>Dinner </button>
                <button className="btn_grp" onClick={() => { Setdata(Sdata) }}>All </button>

            </div>
            <div className="box"> <Card Menudata={Menudata} /> </div>

        </div>
    </>


    )






}

export default App;
