import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import ToDoList from './ToDoList';



const App = ()=>{
    const [ItemInput , SetItemInput] = useState(" ");
    const [AddInput   , SetAddInput] = useState([ ]);
    const InputEvent =(event)=>{
     
        SetItemInput(event.target.value)      
    }
    const AddEvent =()=>{
     
        SetAddInput((oldItems)=> {
         return    [...oldItems, ItemInput]
        })
        SetItemInput('')
    }
    const DeleteItems = (id)=>{
       
        SetAddInput((oldItems)=>{
            return oldItems.filter((arrElem, index)=>{
                return index !==id;
            })
        })
        };
    
return (<>
<div className='container'>
<h1 className='heading'>ToDo List</h1>
<br/>
<input className="inputfield" type="text" onChange ={InputEvent} placeholder="Add Items" value={ItemInput}/>
<button className='add_button' onClick={AddEvent}>+</button>
<br/>
<ol>
    {
        AddInput.map((val, index)=>{
           return <ToDoList key={index}
           id={index}
           
            text={val}
               onSelect={DeleteItems}
           />
            
        })
    }
</ol>
</div>
</>
)






}

export default App;
